import React from 'react';
import {getLocation} from "./utils";
import './App.css';

const API_URL = "https://api.openweathermap.org/data/2.5";
const API_KEY = "840f06a7c96cfaf547124964bde3a17b";



class App extends React.Component {
  state = {
    lon: '',
    lat: ''
  }

  async componentDidMount() {
    const {coords} = await getLocation();
    console.log(coords);
    this.setState((state) => ({...state, lat: coords.latitude.toFixed(2), lon: coords.longitude.toFixed(2)}));
    this.fetchWeather();
  };
fetchWeather = async () => {
  const {lat, lon} = this.state;
  const response = await fetch(`${API_URL}/weather?lat=${lat}&lon=${lon}&appid=${API_KEY}&inuts=metric`);
  const data = response.json();
  console.log(data);
}

  render() {
    return <div className="app__container">
      <div className="app__content">
        {this.state.lon} {this.state.lat} 
      </div>
    </div>
  }
}

export default App;
